import { useState , useEffect } from "react";
import { Redirect } from "react-router";
import ReactStars from "react-rating-stars-component";
import { ToastContainer,toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import {useDispatch } from "react-redux";
import { useHistory } from "react-router-dom";

import { defaultformAction } from "../../store/defaultformstore";
import Navbar from "../Navbar";

export default function Form(){
    // all states
    const [data , setData] = useState({
        isLike : "",
        Comment : "",
    });
    const [redirect , setRedirect] = useState(false);
    const [rate, setRate] = useState(0);

    // variables gobals
    const dispatch = useDispatch();
    let userData = "";
    let history = useHistory();

    useEffect(() => {
        let token = sessionStorage.getItem("Token");
        if(token){
            userData = JSON.parse(sessionStorage.getItem('userInfo'));
            userData = userData._id;
        }else{
            setRedirect(true);
        }

    }, [])


    //change Handlers
    const eventInput = (event) =>{
        const {name , value} = event.target;
        setData((preValue) =>{
            return {
                ...preValue ,
                [name] : value
            }
        })
    }
    const ratingChanged = (newRating) => {
        setRate(newRating);
    };


    //submit handler
    const SubmitHandler = (e) =>{
        e.preventDefault();
        let result = window.confirm("Will you Continue to fill the form....");

        if(!result){
            if(rate === 0 || data.isLike === ""){
                toast.warning('Please Filled The Form Completely', {
                    position: "top-center",
                    autoClose: 2000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                });
            }
            else{
                let defaultData = {
                    DidLike : data.isLike === "Yes" ? true : false,
                    Rating : rate,
                    Comment : data.Comment,
                    UserId : JSON.parse(sessionStorage.getItem('userInfo'))._id,
                    RatingFoods : [],
                    LikeFoods : []
                }
                fetch(process.env.REACT_APP_BACKEND + "/form/form/", {
                    method : 'POST',
                    headers : {
                        'Accept': 'application/json',
                        'Content-Type': 'application/json',
                    },
                    body : JSON.stringify(defaultData)
                }).then(res => res.json())
                .then(fetchdata => {
                    toast.success('Successfully saved', {
                        position: "top-center",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                    });

                    setData({
                        isLike : "",
                        comment : "",
                    })
                    setTimeout(() => {
                        window.location.reload();
                    }, 2000);
                })
                .catch(err => console.log(err))
            }
        }else{
            dispatch(defaultformAction.commentCheck(data.Comment));
            dispatch(defaultformAction.likeCheck(data.isLike));
            dispatch(defaultformAction.ratingCheck(rate));
            setTimeout(() => {
                history.push("/review2");
            }, 2000);
        }
        
        
    }




    return (
        <>
         <Navbar  /> 
        {redirect && <Redirect to="/" />}
        <section className="mainsection">
        <form onSubmit={SubmitHandler} >
        <div className="formbox">
            <div >
                <label for="fname"><h4>Do You Like this food?</h4></label><br/>
                <input 
                type="radio" 
                name="isLike" 
                onChange={eventInput}
                value="Yes"/>
                Yes
                <input 
                type="radio" 
                name="isLike" 
                onChange={eventInput}
                value="no"/>No
                <br/><br/>
            </div>

            <div>
                <label for="fname" className="left-align2"><h4>Rate the overall Food</h4></label><br/>
                <ReactStars
                count={5}
                onChange={ratingChanged}
                size={24}
                isHalf={true}
                emptyIcon={<i className="far fa-star"></i>}
                halfIcon={<i className="fa fa-star-half-alt"></i>}
                fullIcon={<i className="fa fa-star"></i>}
                activeColor="#ffd700"
                />
            </div>

            <div>
                <label for="fname" className="left-align2"><h4>Comments</h4></label><br/>
                <textarea 
                placeholder="Enter Comment"
                value={data.Comment}
                onChange={eventInput}
                rows="4" cols="50" maxlength="200"
                name="Comment"
                >
                </textarea>
            </div>
            <br/><br/>
            <button className="buttons" type="submit">Submit</button>
        </div>
    </form>
    </section>
        <ToastContainer
                position="top-center"
                autoClose={2000}
                hideProgressBar={false}
                newestOnTop={false}
                closeOnClick
                rtl={false}
                pauseOnFocusLoss
                draggable
                pauseOnHover
            />
        </>
    )
}
