import { useState , useEffect } from "react";
import { Redirect } from "react-router";
import Navbar from "../Navbar";
import ReactStars from "react-rating-stars-component";
import { ToastContainer,toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default function Form(){
    // all states
    const [data , setData] = useState({
        isLike : "",
        comment : "",
    });
    const [redirect , setRedirect] = useState(false);
    const [rate, setRate] = useState(0);

    useEffect(() => {
        let token = sessionStorage.getItem("Token");
        if(token){

        }else{
            setRedirect(true);
        }

    }, [])


    //change Handlers
    const eventInput = (event) =>{
        const {name , value} = event.target;
        setData((preValue) =>{
            return {
                ...preValue ,
                [name] : value
            }
        })
    }
    const ratingChanged = (newRating) => {
        setRate(newRating);
    };


    //submit handler
    const SubmitHandler = (e) =>{
        e.preventDefault();
        let result = window.confirm("Will you Continue to fill the form....");
        if(!result){
            if(rate === 0 || data.isLike === ""){
                toast.warning('Please Filled The Form Completely', {
                    position: "top-center",
                    autoClose: 2000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                });
            }
            else{
                console.log(rate , data)
                setData({
                    isLike : "",
                    comment : "",
                })
                window.location.reload();
            }
        }else{
            console.log("continue")
        }
        
        
    }
    return (
        <>
         <Navbar  /> 
        {redirect && <Redirect to="/" />}
        <section className="mainsection">
        <form onSubmit={SubmitHandler} >
        <div className="formbox">
            <div >
                <label for="fname"><h4>Do You Like this food?</h4></label><br/>
                <input 
                type="radio" 
                name="isLike" 
                onChange={eventInput}
                value="Yes"/>
                Yes
                <input 
                type="radio" 
                name="isLike" 
                onChange={eventInput}
                value="no"/>No
                <br/><br/>
            </div>

            <div>
                <label for="fname" className="left-align2"><h4>Rate the overall Food</h4></label><br/>
                <ReactStars
                count={5}
                onChange={ratingChanged}
                size={24}
                isHalf={true}
                emptyIcon={<i className="far fa-star"></i>}
                halfIcon={<i className="fa fa-star-half-alt"></i>}
                fullIcon={<i className="fa fa-star"></i>}
                activeColor="#ffd700"
                />
            </div>

            <div>
                <label for="fname" className="left-align2"><h4>Comments</h4></label><br/>
                <textarea 
                placeholder="Enter Comment"
                value={data.comment}
                onChange={eventInput}
                rows="4" cols="50" maxlength="200"
                name="comment"
                >
                </textarea>
            </div>
            <br/><br/>
            <button className="buttons" type="submit">Submit</button>
        </div>
    </form>
    </section>
        <ToastContainer
                position="top-center"
                autoClose={2000}
                hideProgressBar={false}
                newestOnTop={false}
                closeOnClick
                rtl={false}
                pauseOnFocusLoss
                draggable
                pauseOnHover
            />
        </>
    )
}
